import React from 'react';


import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import firebase from "./firebase";



import * as serviceWorker from './serviceWorker';

import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";

export default class Table extends React.Component{

	constructor(props){
		super(props);
		firebase.ref("/users").push({name: "Dhruv Patil", email:"Dhruv@email.com",bio:"A good programmer"});
		this.state = {
			  names:[],

			  count:1
		};


	}

	 componentDidMount(){
	  const nameRef = firebase.ref('users');
	  nameRef.on('value', (snapshot)=> {
		  let names = snapshot.val();
		  let newState = [];
		  for (let name in names){
			  newState.push({
				  id:name,		 
				  name: names[name].name,
				  email:names[name].email,
				  bio:names[name].bio
			  });
		  }
		  this.setState({
			  names: newState
		  });
	  })
  }

   selectRow = (a,b,c,e) => {
    this.setState({  //to update form data
		formid: a,
		formname:b,
		formemail:c,
		formbio:e
	});
  }

   handleSubmit = (event) => {
    event.preventDefault();
	firebase.ref("/users").child(this.state.formid).update({
	  name: this.state.formname,
	  email: this.state.formemail,
	  bio: this.state.formbio,
	});
  }
   myNameHandler = (event) => {
    this.setState({inputname: event.target.value});
  }
  myEmailHandler = (event) => {
    this.setState({inputemail: event.target.value});
  }
  myBioHandler = (event) => {
    this.setState({inputbio: event.target.value});
  }




	render(){
		return (
		<div>
		<Router>
			<nav class="navbar navbar-inverse">
			  <div class="container-fluid">
				<div class="navbar-header">
				  <a class="navbar-brand" href="#">WebSiteName</a>
				</div>
				<ul class="nav navbar-nav">
				  <li><Link to="/">Home</Link></li>
				  <li><Link to="add">Add</Link></li>


				</ul>
			  </div>
			</nav>
		<Switch>
          <Route exact path="/add">
            <h1>Test</h1>
          </Route>
		</Switch>
		<Switch>
			<Route exact path="/">
				<div class="container">
					<form class="form-inline"  onSubmit={this.handleSubmit}>
					<span>Enter Name</span>
					<input type="text" class="form-control" name="name" />
					<span>Enter Email</span>
					<input type="text" onChange={this.myEmailHandler} class="form-control" name="email"/>
					<span>Enter Bio</span>
					<input type="text" onChange={this.myBioHandler} class="form-control" name="bio"/>
					<input type="submit" class="form-control" name="submit" value="Submit" />

					</form>
					<table class="table">
					  <thead>
						<tr>
						  <th scope="col">#</th>
						  <th scope="col">Name</th>
						  <th scope="col">Email</th>
						  <th scope="col">Bio</th>
						  <th scope="col">Actions</th>
						</tr>
					  </thead>
					  <tbody>
					  {this.state.names.map((name) => {

							return (

								<tr onClick={() => this.selectRow(name.id,name.name,name.email,name.bio)}>
									<td>{this.state.count++}</td>
									<td>{name.name}</td>
									<td>{name.email}</td>
									<td>{name.bio}</td>
									<td>{name.id}</td>
								</tr>
							)

					  })}

					  </tbody>
					</table>
				</div>
			</Route>
		</Switch>
		</Router>
		</div>


		);
	}
}
function Add() {
  return (
    <div>
      <h2>About</h2>
    </div>
  );
}


ReactDOM.render(
  <React.StrictMode>
    <Table />
  </React.StrictMode>,
  document.getElementById('root')
);

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA
serviceWorker.unregister();
